parameters.jsCode:
const emailData = $('Email Trigger').first().json;

function stripHtml(str) {
  return str.replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]*>/g, '');
}

function removeInjectionPatterns(str) {
  return str
    .replace(/\{\{[^}]*\}\}/g, '[removed]')
    .replace(/\$\([^)]*\)/g, '[removed]')
    .replace(/(javascript|onerror|onload|onclick|eval|alert)\s*[:=(]/gi, '[removed]')
    .replace(/<\?xml[^?]*\?>/gi, '')
    .replace(/<!\[CDATA\[[\s\S]*?\]\]>/gi, '');
}

function truncate(str, max) {
  return str.length > max ? str.substring(0, max) + '...[truncated]' : str;
}

const fromRaw = (emailData.from || '').toString();
let senderName = '';
let senderEmail = '';
const fromMatch = fromRaw.match(/^(.+?)\s*<(.+?)>$/);
if (fromMatch) {
  senderName = fromMatch[1].replace(/"/g, '').trim();
  senderEmail = fromMatch[2].trim();
} else {
  senderEmail = fromRaw.trim();
  senderName = fromRaw.trim();
}

let subject = (emailData.subject || '').toString();
let body = (emailData.text || '').toString();

subject = stripHtml(subject);
subject = removeInjectionPatterns(subject);
subject = subject.replace(/\s+/g, ' ').trim();
subject = truncate(subject, 200);

body = stripHtml(body);
body = removeInjectionPatterns(body);
body = body.replace(/\n{3,}/g, '\n\n').trim();
body = truncate(body, 5000);

// Validate: Is this a legitimate stakeholder request?
const emailLower = senderEmail.toLowerCase();
const emailDomain = emailLower.split('@').pop() || '';
const systemDomains = ['google.com', 'youtube.com', 'facebook.com', 'twitter.com', 'linkedin.com', 'microsoft.com', 'apple.com', 'amazon.com', 'paypal.com'];
const systemPatterns = ['noreply', 'no-reply', 'mailer-daemon', 'postmaster', 'notifications', 'newsletter', 'marketing'];
const isSystemEmail = systemDomains.some(d => emailDomain === d || emailDomain.endsWith('.' + d)) || systemPatterns.some(p => emailLower.includes(p));
const hasRequestSubject = subject.toLowerCase().includes('request:');
const isValidRequest = !isSystemEmail && hasRequestSubject;

return {
  sanitizedSubject: subject,
  sanitizedBody: body,
  senderName: senderName,
  senderEmail: senderEmail,
  isValidRequest: isValidRequest
};

const aiResponse = $('Parse Email with AI').first().json;
let aiResultText = aiResponse.text || aiResponse.response || '';

aiResultText = aiResultText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

let aiResult;
try {
  aiResult = JSON.parse(aiResultText);
} catch (e) {
  aiResult = {};
}

const sanitizedData = $('Sanitize Email Data').first().json;

const newId = `${Date.now().toString(36)}-${Math.random().toString(36).substr(2, 9)}`;

const newTask = {
  id: newId,
  name: aiResult.title || sanitizedData.sanitizedSubject,
  description: aiResult.description || sanitizedData.sanitizedBody + '\n\nDieses Ticket wurde KI-generiert.',
  assigned_to: '',
  due_date: aiResult.deadline || '',
  prio: aiResult.priority || 2,
  category: aiResult.category || 'us',
  subtasks: '',
  status: 'triage',
  creator: {
    name: sanitizedData.senderName,
    email: sanitizedData.senderEmail,
    type: 'external'
  },
  ai_generated: true,
  created_at: new Date().toISOString()
};

return {
  newTask: newTask,
  taskId: newId
};

